import React from 'react';
import ReactDOM from 'react-dom';

const Header = (props) => {
  return(
  <> 
    <h1>{props.course}</h1>
  </>
  )
}
const Content = (props) => {
  return (
    <>
      <Part part={props.part} exercise={props.exercise} />
    </>
  )
}
const Part = (props) => {
  return(
  <p>{props.part} {props.exercise}</p>
  )
}
const Total = (props) => {
  console.log(props, props.exercises)
  let totalExer = 0
  props.exercises.forEach(element => {
    totalExer += element
  });
  return (
    <>
      <p>Number of exercises {totalExer}</p>
    </>
  )
}
const App = () => {
  const course = 'Half Stack application development'
  const parts = [
    {
      name: 'Fundamentals of React',
      exercises: 10
    },
    {
      name: 'Using props to pass data',
      exercises: 7
    },
    {
      name: 'State of a component',
      exercises: 14
    }
  ]

  return (
    <div>
      <Header course={course} />
      <Content part={parts[0].name} exercise={parts[0].exercises} />
      <Content part={parts[1].name} exercise={parts[1].exercises} />
      <Content part={parts[2].name} exercise={parts[2].exercises} />
      <Total exercises={[parts[0].exercises, parts[1].exercises, parts[2].exercises]} />
    </div>  
  )
}



ReactDOM.render(<App />, document.getElementById('root'));
